import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers } from '../../redux/users/actions';
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableHead from "@material-ui/core/TableHead";
import TableBody from "@material-ui/core/TableBody";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import TablePagination from '@material-ui/core/TablePagination';
import { makeStyles } from '@material-ui/core/styles';
import UserItem from './UserItem';
import Loader from '../../common/Loader';

const UsersList = () => {
  const dispatch = useDispatch();

  const { list, loading } = useSelector(state => state.users);


  useEffect(() => {
    dispatch(getUsers());
  }, [dispatch])

  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);

  const headRows = [
    { id: 'avatar', numeric: false, label: 'User avatar' },
    { id: 'login', numeric: false, label: 'Login' },
    { id: 'profile link', numeric: false, label: 'Profile link' },
  ];

  return <div>
    <Paper className='m020'>
      <Table>
        <TableHead >
          <TableRow className='tableHead'>
            {headRows.map(row => (
              <TableCell
                key={row.id}
                align={row.numeric ? 'right' : 'left'}
              >
                {row.label}
              </TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {loading ? <Loader /> :
            list?.data?.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((user) => (
              <UserItem key={user.id} user={user} />
            ))}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[5, 10, 15]}
        component="div"
        count={list?.data?.length}
        rowsPerPage={rowsPerPage}
        page={page}
        backIconButtonProps={{
          'aria-label': 'previous page',
        }}
        nextIconButtonProps={{
          'aria-label': 'next page',
        }}
        onChangePage={(event, pageNum) => setPage(pageNum)}
        onChangeRowsPerPage={(event) => { setRowsPerPage(+event.target.value); setPage(0) }}
      />
    </Paper>
  </div>;
};

export default UsersList;
