import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { getUsers } from '../../redux/users/actions';
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    large: {
        width: theme.spacing(12.5),
        height: theme.spacing(12.5),
    },
}));


const User = ({ user }) => {
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getUsers());
    }, [dispatch])

    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(5);

    const headRows = [
        { id: 'avatar', numeric: false, label: 'User avatar' },
        { id: 'login', numeric: false, label: 'Login' },
        { id: 'profile link', numeric: false, label: 'Profile link' },
    ];

    return
        <TableRow key={user.id}>
            <TableCell align="left"><Avatar alt="Remy Sharp" src={user.avatar_url} className={classes.large} /></TableCell>
            <TableCell align="left">{user.login}</TableCell>
            <TableCell align="left"><a href={user.html_url} target="_blank" rel="noopener noreferrer">{user.html_url}</a></TableCell>
        </TableRow>
};

export default User;
