import React, { useState } from 'react'; 
import { Link } from 'react-router-dom';
import routes from '../../../routes';

import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    large: {
        width: theme.spacing(12.5),
        height: theme.spacing(12.5),
    },
    color: { color: '#224456', },
}));

const UserItem = ({ user }) => {
    const classes = useStyles();

    const [isHovered, setIsHovered] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(!isHovered);
    }

    const handleMouseLeave = () => {
        setIsHovered(!isHovered);
    }

    return (
        <TableRow key={user.id} hover
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        >
            {isHovered && <TableCell align="left"><Avatar alt="Remy Sharp" src={user.avatar_url} className={classes.large} /></TableCell> }
            <TableCell align="left"><Link to={`${routes.UserProfile.path}${user.login}`}>{user.login}</Link></TableCell>
            <TableCell align="left"><a href={user.html_url} target="_blank" rel="noopener noreferrer">{user.html_url}</a></TableCell>
        </TableRow>
    )
};

export default UserItem;
