import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers } from '../../redux/users/actions';
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableHead from "@material-ui/core/TableHead";
import TableBody from "@material-ui/core/TableBody";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import TablePagination from '@material-ui/core/TablePagination';

const UsersList = () => {
  const dispatch = useDispatch();

  const { list } = useSelector(state => state.users);


  useEffect(()=>{
    dispatch(getUsers());
  },[])

  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);

  const headRows = [
    { id: 'id2', numeric: false, label: 'id2' },
    { id: 'name2', numeric: true, label: 'name2' },
    { id: 'surname', numeric: true, label: 'surname' },
    { id: 'desc', numeric: true, label: 'desc' },
    { id: 'edit', numeric: true, label: 'Редактировать' },
    { id: 'delete', numeric: true, label: 'Удалить' },
  ];

  return <div>
    <Paper className='m020'>
      <Table>
        <TableHead >
          <TableRow className='tableHead'>
            {headRows.map(row => (
              <TableCell
                key={row.id}
                align={row.numeric ? 'right' : 'left'}
              >
                {row.label}
              </TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {list?.data?.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((user, num) => (
            <TableRow key={user.id}>
              <TableCell align="left">{user.id}</TableCell>
              <TableCell align="right">{user.name}</TableCell>
              <TableCell align="right">{user.surname}</TableCell>
              <TableCell align="right">{user.desc}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[5, 10, 15]}
        component="div"
        count={users.length}
        rowsPerPage={rowsPerPage}
        page={page}
        backIconButtonProps={{
          'aria-label': 'previous page',
        }}
        nextIconButtonProps={{
          'aria-label': 'next page',
        }}
        onChangePage={(event, pageNum) => setPage(pageNum)}
        onChangeRowsPerPage={(event) => { setRowsPerPage(+event.target.value); setPage(0) }}
      />
    </Paper>
  </div>;
};

export default UsersList;
