import React, { useState } from 'react'; 
import { Link } from 'react-router-dom';
import routes from '../../../routes';

import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import Avatar from '@material-ui/core/Avatar';
import Popover from '@material-ui/core/Popover';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    large: {
        width: theme.spacing(12.5),
        height: theme.spacing(12.5),
    },
    color: { color: '#224456', },
}));

const UserItem = ({ user }) => {
    const classes = useStyles();

    const [isHovered, setIsHovered] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(!isHovered);
    }

    const handleMouseLeave = () => {
        setIsHovered(!isHovered);
    }

    return (
        <TableRow key={user.id} hover
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        >
            {isHovered && <TableCell align="left"><Avatar alt="Remy Sharp" src={user.avatar_url} className={classes.large} /></TableCell> }
            <TableCell align="left"><Link to={`${routes.UserProfile.path}${user.login}`}>{user.login}</Link></TableCell>
            <Typography
        aria-owns={open ? 'mouse-over-popover' : undefined}
        aria-haspopup="true"
        onMouseEnter={handlePopoverOpen}
        onMouseLeave={handlePopoverClose}
      >
        Hover with a Popover.
      </Typography>
      <Popover
        id="mouse-over-popover"
        className={classes.popover}
        classes={{
          paper: classes.paper,
        }}
        open={open}
        anchorEl={anchorEl}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'left',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'left',
        }}
        onClose={handlePopoverClose}
        disableRestoreFocus
      >
        <Typography><Avatar alt={user.avatar_url} src={user.avatar_url} className={classes.large} /></Typography>
      </Popover>
            <TableCell align="left"><a href={user.html_url} target="_blank" rel="noopener noreferrer">{user.html_url}</a></TableCell>
        </TableRow>
    )
};

export default UserItem;
